import UIKit

protocol CreateUserBusinessLogic
{
  func doSomething(request: CreateUser.Something.Request)
  func createOrder(request: CreateUser.Something.Request)

}

protocol CreateUserDataStore
{
  //var name: String { get set }
}

class CreateUserInteractor: CreateUserBusinessLogic, CreateUserDataStore
{
  var presenter: CreateUserPresentationLogic?
  var worker: CreateUserWorker?
  //var name: String = ""
  
  // MARK: Do something
    func createOrder(request: CreateUser.Something.Request) {
        worker = CreateUserWorker()
        worker?.doSomeWork()
        
        let userObj = Users(first_name: request.createUserFields.firstname, email: request.createUserFields.email, last_name: request.createUserFields.lastname)
        
        CoreDataManager.sharedInstance.createUser(userData: userObj, compleationHandler: { (responce) -> Void in
            let response = CreateUser.Something.Response()
            self.presenter?.presentSomething(response: response)
        })

    }
  
  func doSomething(request: CreateUser.Something.Request)
  {
    worker = CreateUserWorker()
    worker?.doSomeWork()
    
    let response = CreateUser.Something.Response()
    presenter?.presentSomething(response: response)
  }
}
